import React, { useState } from 'react';
import {
    Card, CardImg, CardText, CardBody, CardTitle, Breadcrumb, BreadcrumbItem,
    Button, Form, FormGroup, Label, Col, Modal, ModalHeader, ModalBody
} from 'reactstrap';
import { Link } from 'react-router-dom';
import { useForm } from "react-hook-form";

const CommentForm = () => {
    const { register, handleSubmit, watch, formState: { errors }, reset } = useForm(
        {
            mode: 'onChange',
            defaultValues: {
                raiting: "1",
                name: "",
                comment: ""
            }
        }
    );
    const [isModalOpen, setIsModalOpen] = useState(false);

    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);
    }

    const onSubmit = data => {
        alert(JSON.stringify(data));
        toggleModal();
        reset();
        //e.preventDefault();
    }

    const closeBtn = <button className="close" onClick={toggleModal}>&times;</button>;

    return (
        <div className="row">
            <Col offset={2}>
                <Button onClick={toggleModal} color="secondary" outline><i className="fa fa-pencil"></i> Submit Comment</Button>
            </Col>
            <Modal isOpen={isModalOpen} toggle={toggleModal}>
                <ModalHeader toggle={toggleModal} close={closeBtn}>Submit Comment</ModalHeader>
                <ModalBody>
                    <Form onSubmit={handleSubmit(onSubmit)}>
                        <FormGroup>
                            <Label htmlFor="raiting">Raiting</Label>
                            <select className="form-select form-control" {...register("raiting")}>
                                <option value="1">1</option>
                                <option value="2">2</option>
                                <option value="3">3</option>
                                <option value="4">4</option>
                                <option value="5">5</option>
                            </select>
                        </FormGroup>
                        <FormGroup>
                            <Label htmlFor="name">Your Name</Label>
                            <input type="text" className="form-control"
                                {...register("name", {
                                    maxLength: {
                                        value: 15,
                                        message: 'Must be 15 characters or less'
                                    },
                                    minLength: {
                                        value: 3,
                                        message: 'Must be greater than 3 characters'
                                    }
                                })}
                            ></input>
                            <span role="alert" className="text-danger">{errors.name?.message}</span>
                        </FormGroup>
                        <FormGroup>
                            <Label htmlFor="comment">Comment</Label>
                            <textarea rows={6} className="form-control" {...register("comment")}></textarea>
                        </FormGroup>
                        <Button type="submit" value="Submit" color="primary">Comment</Button>
                    </Form>
                </ModalBody>
            </Modal>
        </div>
    );
}





function RenderDish({ dish }) {
    return (
        <React.Fragment>
            <Card>
                <CardImg top src={dish.image} alt={dish.name} />
                <CardBody>
                    <CardTitle>{dish.name}</CardTitle>
                    <CardText>{dish.description}</CardText>
                </CardBody>
            </Card>
        </React.Fragment>
    );
}

function RenderComments({ comments }) {
    if (comments != null) {
        const listItems = comments.map((comment) => {
            return (
                <li key={comment.id}>
                    <p>{comment.comment}</p>
                    <p>-- {comment.author}, {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit' }).format(new Date(Date.parse(comment.date)))}</p>
                </li>
            )
        });

        return (
            <React.Fragment>
                <h4>Comments</h4>
                <ul className="list-unstyled">{listItems}</ul>
                <CommentForm bg-primary><span className="fa fa-pencil">Submit Comment</span></CommentForm>
            </React.Fragment>
        );
    }
    else {
        return (
            <div></div>
        );
    }
}



function RenderDishDetail({ dish, comments }) {

    return (
        <div className="container">
            <div className="row">
                <Breadcrumb>
                    <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                    <BreadcrumbItem active>{dish.name}</BreadcrumbItem>
                </Breadcrumb>
                <div className="col-12">
                    <h3>{dish.name}</h3>
                    <hr />
                </div>
            </div>
            <div className="row">
                <div className="col-12 col-md-5 m-1">
                    <RenderDish dish={dish} />
                </div>
                <div className="col-12 col-md-5 m-1">
                    <RenderComments comments={comments} />
                </div>
            </div>
        </div>
    );

}


function DishDetail(props) {

    return (

        <div className="container">
            <div className="row">
                <RenderDishDetail dish={props.dish} comments={props.comments} />
            </div>
        </div>


    );

}


export default DishDetail;
